//
// Browser detection
var isMobile = /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent);
var isIOS = /iPad|iPhone|iPod/.test(navigator.userAgent);
var isOldAndroid = /Android 4\.|Android 5\.0/.test(navigator.userAgent);

// Set a flag for use later
window.isMobileDevice = isMobile;
window.isIOSDevice = isIOS;
window.isOldAndroidDevice = isOldAndroid;

// Force table rendering mode for problematic devices
if (isOldAndroid || isIOS) {
    document.documentElement.className += ' force-simplified-mode';
}
