// Detect mobile devices and optimize layout
document.addEventListener('DOMContentLoaded', function() {
  var isMobile = window.innerWidth < 768;
  if (isMobile) {
    // Add a hint for users on mobile
    var mobileHint = document.createElement('div');
    mobileHint.style.backgroundColor = '#fff3cd';
    mobileHint.style.padding = '8px';
    mobileHint.style.marginBottom = '10px';
    mobileHint.style.textAlign = 'center';
    mobileHint.innerHTML = 'Scroll horizontally to see all data columns →';

    var table = document.querySelector('.report-table');
    if (table && table.parentNode) {
      table.parentNode.insertBefore(mobileHint, table);
    }
  }
});
//

//
function forceSimpleMode() {
    // Add simplified mode class
    document.documentElement.className += ' force-simplified-mode';

    // Get table and wrap it
    var table = document.querySelector('.report-table');
    if (table && table.parentNode) {
        var wrapper = document.createElement('div');
        wrapper.className = 'report-table-wrapper';
        table.parentNode.insertBefore(wrapper, table);
        wrapper.appendChild(table);

        // Force table to basic styling
        table.style.width = 'auto';
        table.style.minWidth = '800px';

        // Hide emergency message
        var emergency = document.querySelector('.emergency-fallback');
        if (emergency) {
            emergency.style.display = 'none';
        }
    }
}

// Check if we should show emergency fallback
document.addEventListener('DOMContentLoaded', function() {
    setTimeout(function() {
        // If table cells aren't visible after 1 second, show emergency message
        var cells = document.querySelectorAll('.report-table td');
        var tableVisible = cells.length > 0 && 
                          window.getComputedStyle(cells[0]).display !== 'none';

        if (!tableVisible || window.isOldAndroidDevice || 
            (window.isIOSDevice && window.innerWidth < 375)) {
            document.querySelector('.emergency-fallback').style.display = 'block';
        }
    }, 1000);
});
//

//
// Samsung browser hack
var isSamsung = /SamsungBrowser/i.test(navigator.userAgent);
if (isSamsung) {
    document.addEventListener('DOMContentLoaded', function() {
        // Force redraw by toggling a class
        document.body.classList.add('samsung-browser');
        setTimeout(function() {
            document.body.classList.remove('samsung-browser');
        }, 50);

        // Fix position:sticky which can be problematic
        var stickyElements = document.querySelectorAll('[style*="position: sticky"]');
        stickyElements.forEach(function(el) {
            el.style.position = 'fixed';
        });
    });
}
